import React from 'react'

function Header({ setIsAdding }) {
    return (
        <header style={headerStyle}>
            <h1 style={headingStyle}>Employee Management Software</h1>
            <div style={buttonContainerStyle}>
                <button onClick={() => setIsAdding(true)} style={buttonStyle}>Add An Employee</button>
            </div>
        </header>
    )
}

const headerStyle = {
  backgroundColor: '#333',
  color: '#fff',
  padding: '20px',
  textAlign: 'center',
}

const headingStyle = {
  fontSize: '36px',
  margin: '0',
}

const buttonContainerStyle = {
  display: 'flex',
  justifyContent: 'center',
}

const buttonStyle = {
  backgroundColor: '#fff',
  border: 'none',
  color: '#333',
  padding: '10px 20px',
  borderRadius: '25px',
  cursor: 'pointer',
  fontWeight: 'bold',
  fontSize: '18px',
}

export default Header
