import React from 'react';

function Header({ setIsAdding }) {
    return (
        <header style={headerStyle}>
            <h1 style={headingStyle}>Employee Management Software</h1>
            <div style={buttonContainerStyle}>
                <button onClick={() => setIsAdding(true)} style={buttonStyle}>Add An Employee</button>
            </div>
        </header>
    )
}

const headerStyle = {
    backgroundImage: "url('/background-image.jpg')",
    backgroundSize: 'cover',
    height: '400px',
    display: 'flex',
    flexDirection: 'column',
    justifyContent: 'center',
    alignItems: 'center',
    textAlign: 'center',
}

const headingStyle = {
    fontSize: '50px',
    margin: '0',
    color: '#fff',
}

const buttonContainerStyle = {
    marginTop: '30px',
    marginBottom: '18px',
}

const buttonStyle = {
    backgroundColor: '#fff',
    border: 'none',
    color: '#333',
    padding: '15px 30px',
    borderRadius: '25px',
    cursor: 'pointer',
    fontWeight: 'bold',
    fontSize: '20px',
}

export default Header;
